import { Component } from "react";

const styles = {
    producto: {
        border: 'solid 1px #EEE'
    }
}
class Producto extends Component{
    render(){
        const { producto } = this.props
        return(
            <div style= {styles.producto}>
                <img alt={producto.name} src={producto.img}></img>
                <h3>{producto.name}</h3>
                <p>{producto.price}</p>
            </div>
        )
    }
}

export default Producto
